sys_script.script:
(function executeRule(current, previous /*null when async*/) {




	if (current.u_slx_estado_encuesta ==1 || current.u_slx_estado_encuesta==3){
		current.u_slx_encuesta_respondida.setDisplayValue('Si');
	}else if(current.u_slx_estado_encuesta ==2){
		current.u_slx_encuesta_respondida.setDisplayValue('No');
	}

})(current, previous);
