sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	if (current.u_slx_estado_encuesta ==1 ){
		current.u_slx_encuesta_firmada='Si';
	}else if(current.u_slx_estado_encuesta ==2 || current.u_slx_estado_encuesta==3){
		current.u_slx_encuesta_firmada='No';
	}
})(current, previous);
